import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Admin.css"; // Ensure this file exists for styling

function Admin() {
  const [orders, setOrders] = useState([]);
  const [juices, setJuices] = useState([]);
  const [completedOrders, setCompletedOrders] = useState([]);
  const [newJuice, setNewJuice] = useState({ name: "", price: "" });

  useEffect(() => {
    fetchOrders();
    fetchJuices();
    fetchCompletedOrders(); // Fetch completed orders
  }, []);

  const fetchOrders = () => {
    axios.get("http://localhost:5000/orders")
      .then((res) => setOrders(res.data))
      .catch((err) => console.error("Error fetching orders", err));
  };

  const fetchJuices = () => {
    axios.get("http://localhost:5000/juices")
      .then((res) => setJuices(res.data))
      .catch((err) => console.error("Error fetching juices", err));
  };

  const fetchCompletedOrders = () => {
    axios.get("http://localhost:5000/completed-orders")
      .then((res) => setCompletedOrders(res.data))
      .catch((err) => console.error("Error fetching completed orders", err));
  };

  const addJuice = () => {
    if (!newJuice.name || !newJuice.price) return alert("Please enter all fields");

    axios.post("http://localhost:5000/juices", { ...newJuice, date: new Date().toISOString() })
      .then(() => {
        fetchJuices();
        setNewJuice({ name: "", price: "" });
      })
      .catch((err) => console.error("Error adding juice", err));
  };

  const deleteJuice = (id) => {
    axios.delete(`http://localhost:5000/juices/${id}`)
      .then(() => fetchJuices())
      .catch((err) => console.error("Error deleting juice", err));
  };

  const completeOrder = (id) => {
    const orderToComplete = orders.find(order => order.id === id);

    if (!orderToComplete) return;

    axios.post("http://localhost:5000/complete-order", {
      ...orderToComplete,
      completedDate: new Date().toISOString(),
    })
      .then(() => {
        fetchOrders(); // Refresh orders list
        fetchCompletedOrders(); // Refresh completed orders list
      })
      .catch((err) => console.error("Error completing order", err));
  };

  return (
    <div>
      <h2>Admin Panel - Orders</h2>

      <table border="1">
        <thead>
          <tr>
            <th>ID</th>
            <th>Customer</th>
            <th>Phone</th>
            <th>Items</th>
            <th>Date</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.name}</td>
              <td>{order.phone}</td>
              <td>
                <ul>
                  {order.items.map((item, i) => (
                    <li key={i}>{item.name} - ${item.price}</li>
                  ))}
                </ul>
              </td>
              <td>{new Date(order.date).toLocaleString()}</td>
              <td>
                <button onClick={() => completeOrder(order.id)}>Complete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Juices</h2>
      <table border="1">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price ($)</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {juices.map((juice) => (
            <tr key={juice.id}>
              <td>{juice.id}</td>
              <td>{juice.name}</td>
              <td>{juice.price}</td>
              <td>{new Date(juice.date).toLocaleString()}</td>
              <td>
                <button onClick={() => deleteJuice(juice.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Add Juice</h3>
      <input
        type="text"
        placeholder="Juice Name"
        value={newJuice.name}
        onChange={(e) => setNewJuice({ ...newJuice, name: e.target.value })}
      />
      <input
        type="number"
        placeholder="Price"
        value={newJuice.price}
        onChange={(e) => setNewJuice({ ...newJuice, price: e.target.value })}
      />
      <button onClick={addJuice}>Add Juice</button>

      <h2>Completed Orders</h2>
      <table border="1">
        <thead>
          <tr>
            <th>ID</th>
            <th>Customer</th>
            <th>Phone</th>
            <th>Items</th>
            <th>Completed Date</th>
          </tr>
        </thead>
        <tbody>
          {completedOrders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.name}</td>
              <td>{order.phone}</td>
              <td>
                <ul>
                  {order.items.map((item, i) => (
                    <li key={i}>{item.name} - ${item.price}</li>
                  ))}
                </ul>
              </td>
              <td>{new Date(order.completedDate).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Admin;
